def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def httpResponseHeaders = context.testCase.testSteps["startSession"].testRequest.response.responseHeaders
def httpStatus = httpResponseHeaders["#status#"]
def httpStatusCode = (httpStatus =~ "[1-5]\\d\\d")[0]
def strHttpDate = httpResponseHeaders["Date"]

// formatoAParsear [Sat, 05 Dec 2009 17:47:38 GMT], es un array de 1 elemento
// debemos obtener Created=2009-12-05T17:47:38+00:00
// debemos obtener Expires=2009-12-05T23:00:00+00:00
// La fecha es identica en ambos.
// convierto a Array y los separo por espacio vacio

httpDateArray = strHttpDate[0].split(" ")

diaNombre = httpDateArray[0]
dia = httpDateArray[1]
mesNombre = httpDateArray[2]
anyo = httpDateArray[3]
hora = httpDateArray[4]
tiempoZona = httpDateArray[5]

mes = '00'
if (mesNombre == 'Jan'){mes = '01'}
if (mesNombre == 'Feb'){mes = '02'}
if (mesNombre == 'Mar'){mes = '03'}
if (mesNombre == 'Apr'){mes = '04'}
if (mesNombre == 'May'){mes = '05'}
if (mesNombre == 'Jun'){mes = '06'}
if (mesNombre == 'Jul'){mes = '07'}
if (mesNombre == 'Aug'){mes = '08'}
if (mesNombre == 'Sep'){mes = '09'}
if (mesNombre == 'Oct'){mes = '10'}
if (mesNombre == 'Nov'){mes = '11'}
if (mesNombre == 'Dec'){mes = '12'}

def FechaYYYYMMDD = anyo + "-" + mes + "-" + dia
def CreatedToBeTransferred = FechaYYYYMMDD + "T" + hora + "+00:00"
def ExpiresToBeTransferred = FechaYYYYMMDD + "T23:00:00+00:00"

log.info("-> HTTP Status: " + httpStatusCode)
log.info("-> HTTP Date: " + strHttpDate)
log.info("-> Created: " + CreatedToBeTransferred)
log.info("-> YYYY-MM-DD: " + FechaYYYYMMDD)

